es) {},\\r\\n			fail: function (res) {}\\r\\n		});\\r\\n		wx.onMenuShareQQ({\\r\\n			title: shareData.tTitle,\\r\\n			desc:  shareData.tContent,\\r\\n			link:  shareurl,\\r\\n			imgUrl: imgUrl,\\r\\n			trigger: function (res) {},\\r\\n			success: function (res) {},\\r\\n			cancel: function (res) {},\\r\\n			fail: function (res) {}\\r\\n		});\\r\\n		wx.onMenuShareWeibo({\\r\\n			title: shareData.tTitle,\\r\\n			desc:  shareData.tContent,\\r\\n			link:  shareurl,\\r\\n			imgUrl: imgUrl,\\r\\n			trigger: function (res) {},\\r\\n			success: function (res) {},\\r\\n			cancel: function (res) {},\\r\\n			fail: function (res) {}\\r\\n		});\\r\\n	});\\r\\n&lt;/script&gt;\\r\\n</pre>\\r\\n<p>\\r\\n	然后看PHP生成代码的部分。\\r\\n</p>\\r\\n<pre class=\\\"brush:php;\\\">&lt;?\\r\\n	//get token\\r\\n	$token = fopen(\\\"https://api.weixin.qq.com/cgi-bin/token?grant_type=client_credential&amp;appid=wx23223532323232&amp;secret=54947456456465456465465464\\\",\\\"rb\\\");\\r\\n	$tokenRes = \\\"\\\";\\r\\n		\\r\\n	while (!feof($token)) {  $tokenRes .= fread($token, 1000); }\\r\\n\\r\\n	fclose($token);\\r\\n	$token_res = json_decode($tokenRes)-&gt;access_token;\\r\\n\\r\\n	//get ticket\\r\\n	$ticket = fopen(\\\"https://api.weixin.qq.com/cgi-bin/ticket/getticket?access_token=\\\". $token_res .\\\"&amp;type=jsapi\\\",\\\"rb\\\");\\r\\n	$ticketRes = \\\"\\\";\\r\\n	while (!feof($ticket)) { $ticketRes .= fread($ticket, 1000); }\\r\\n\\r\\n	fclose($ticket);\\r\\n	$ticket_res = json_decode($ticketRes)-&gt;ticket;\\r\\n\\r\\n	//create nonceStr\\r\\n	$allChar = \\\"abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789\\\";\\r\\n    $nonceStr = \\\"\\\";\\r\\n    for ($i = 0; $i &lt; 16; $i++) {\\r\\n      $nonceStr .= substr($allChar, mt_rand(0, strlen($allChar) - 1), 1);\\r\\n    }\\r\\n\\r\\n	//create timestamp\\r\\n	$timestamp = time();\\r\\n\\r\\n	//get signature\\r\\n	$tpStr = \\\'jsapi_ticket=\\\'.$ticket_res.\\\'&amp;noncestr=\\\'.$nonceStr.\\\'&amp;timestamp=\\\'.$timestamp.\\\'&amp;url=