INSERT INTO `scrapy_yizhiyuan` VALUES (201, '面向对象程序---JS上传前显示图片缩略图_兼容IE+FF+Chrome', '(\'KBdancer\',\' 发表于 2013-05-04, 12:30 AM. \\n发表在：\\n\')', '(\'<div class=\\\"post-body\\\">\\n<pre class=\\\"brush:js;\\\">&lt;!DOCTYPE html PUBLIC \\\"-//W3C//DTD XHTML 1.0 Transitional//EN\\\" \\\"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\\\"&gt;\\r\\n&lt;html xmlns=\\\"http://www.w3.org/1999/xhtml\\\"&gt;\\r\\n&lt;head&gt;\\r\\n&lt;meta http-equiv=\\\"Content-Type\\\" content=\\\"text/html; charset=utf-8\\\" /&gt;\\r\\n&lt;title&gt;JS上传前显示图片缩略图&lt;/title&gt;\\r\\n&lt;style type=\\\"text/css\\\"&gt;\\r\\n#upload_file{ display:none;}\\r\\n#show_img{/*图片上下左右居中*/\\r\\nwidth:300px; height:300px;\\r\\n/*display:table-cell;\\r\\ntext-align:center; vertical-align:middle;\\r\\nline-height:300px;\\r\\n*float:left; *font-size:300px; *font-family:SimSun;*/	/*for IE6,7*/\\r\\nborder:1px solid green; padding:1px;\\r\\n}\\r\\n #show_img img{ max-width:300px; max-height:300px; vertical-align:middle;}\\r\\n#prevImg{ visibility:hidden;}/*必须是visible,display:none不可以*/\\r\\n&lt;/style&gt;\\r\\n&lt;script type=\\\"text/javascript\\\"&gt;\\r\\n\\r\\nfunction id(id)	//将获取ID元素方法封装\\r\\n{\\r\\nreturn document.getElementById(id);\\r\\n}\\r\\n\\r\\nLocalUpload.prototype.zoomImg=function (obj, imaxW, imaxH, iOriW, iOriH)	//对选取图片按等比列缩放及在容器中居中\\r\\n{\\r\\nvar oZoomImg={width:0, height:0, left:0, top:0};\\r\\nvar rateW=iOriW/imaxW;\\r\\nvar rateH=iOriH/imaxH;\\r\\nif(rateW &gt; rateH)\\r\\n{\\r\\noZoomImg.width=imaxW;\\r\\noZoomImg.height=Math.round(iOriH/rateW);\\r\\n}\\r\\nelse if(rateW &lt;= rateH)\\r\\n{\\r\\noZoomImg.height=imaxH;\\r\\noZoomImg.width=Math.round(iOriW/rateH);	\\r\\n}\\r\\noZoomImg.left=parseInt((imaxW-oZoomImg.width)/2);\\r\\noZoomImg.top=parseInt((imaxH-oZoomImg.height)/2);\\r\\nobj.style.width=oZoomImg.width+\\\'px\\\';\\r\\nobj.style.height=oZoomImg.height+\\\'px\\\';	 \\r\\nobj.style.marginLeft=oZoomImg.left+\\\'px\\\'\\r\\nobj.style.marginTop=oZoomImg.top+\\\'px\\\';\\r\\n};\\r\\n\\r\\nLocalUpload.prototype.popFile=function()	//模拟表单type=\\\'file\\\'弹出本地选择图片弹出框\\r\\n{\\r\\nthis.oFile.click();	//用自定义按钮模拟form默认图片上传表单\\r\\n}\\r\\n\\r\\nLocalUpload.prototype.chooseFile=function (oFile)	//获取选择图片的路径并将其赋值给相应显示的容器里的img\\r\\n{\\r\\nvar oImg=document.createElement(\\\'img\\\');\\r\\nvar _this=this;	//this指的是LocalUpload对象\\r\\nvar sImgSrc=oFile.value;\\r\\nif(_this.checkImgType(sImgSrc))	//判断是否是符合格式的图片\\r\\n{\\r\\nif(_this.getFileSize(oFile))	//判断上传图片大小是否在限制范围内\\r\\n{\\r\\nif(oFile.files)	//FF,Chrome方式\\r\\n{	\\r\\noImg.onload=function()\\r\\n{	\\r\\n_this.zoomImg(oImg, _this.iMaxW, _this.iMaxH, oImg.offsetWidth, oImg.offsetHeight);	\\r\\n}	\\r\\nvar oFileReader=new FileReader();\\r\\noFileReader.onload=function(ev)\\r\\n{\\r\\noImg.src=ev.target.result;	\\r\\n}\\r\\noFileReader.readAsDataURL(oFile.files[0]);\\r\\n}\\r\\nelse	//IE方式\\r\\n{\\r\\noImg.src=sImgSrc;\\r\\n_this.oPrevImg.src=sImgSrc;\\r\\n_this.zoomImg(oImg, _this.iMaxW, _this.iMaxH, _this.oPrevImg.offsetWidth, _this.oPrevImg.offsetHeight);\\r\\n}\\r\\n_this.oDiv.appendChild(oImg);	\\r\\n}\\r\\nelse\\r\\n{\\r\\nalert(\\\'请上传图片大小在5M以内的图片\\\');\\r\\nreturn ;\\r\\n}\\r\\n}\\r\\nelse\\r\\n{	\\r\\nalert(\\\'请上传格式为：jpg,jpeg,gif,png,bpm格式的图片\\\');\\r\\nreturn ;\\r\\n}\\r\\n}\\r\\n\\r\\nLocalUpload.prototype.getFileSize=function(oFile)	//获取图片的空间大小\\r\\n{\\r\\nvar iFileSize=0;\\r\\nif(oFile.files)	//FF\\r\\n{\\r\\n//num.toFixed将num保留2为小数\\r\\n//iFileSize=((oFile.files[0].size/1024)/1024).toFixed(2);\\r\\niFileSize=oFile.files[0].size;\\r\\n}\\r\\nelse\\r\\n{\\r\\n//IE可以用，不足之处会有安全提示，把文件名改为.hta则会屏蔽掉安全提示\\r\\nvar obj=new ActiveXObject(\\\"Scripting.FileSystemObject\\\");	\\r\\niFileSize=obj.GetFile(oFile.value).size;\\r\\n}\\r\\nif(iFileSize&lt;this.iLimitSize)\\r\\n{\\r\\nreturn true;\\r\\n}\\r\\nelse\\r\\n{\\r\\nreturn false;	\\r\\n}\\r\\n}\\r\\n\\r\\nLocalUpload.prototype.checkImgType=function(src)	//检查选取图片的格式\\r\\n{\\r\\nvar bType=true;\\r\\nif(!src.match(/(.jpg|.gif|.png|.jpeg|.bpm)$/i))\\r\\n{\\r\\nbType=false;\\r\\n}\\r\\nreturn bType;\\r\\n}\\r\\n\\r\\nfunction LocalUpload(obj)	//本地上传构造函数\\r\\n{\\r\\nthis.oBtn=id(obj.oUploadBtn);\\r\\nthis.oFile=id(obj.oFile);\\r\\nthis.oDiv=id(obj.oShowDiv);\\r\\nthis.oPrevImg=id(obj.oPrevImg);\\r\\nthis.iMaxW=obj.iMaxW;\\r\\nthis.iMaxH=obj.iMaxH;\\r\\nthis.iLimitSize=obj.iLimitSize;\\r\\nvar _this=this;\\r\\nthis.oBtn.onclick=function()\\r\\n{	\\r\\n_this.popFile();\\r\\n};\\r\\nthis.oFile.onchange=function()\\r\\n{\\r\\n_this.chooseFile(this);\\r\\n};\\r\\n}	\\r\\nwindow.onload=function()\\r\\n{\\r\\nnew LocalUpload({\\r\\noUploadBtn:\\\'upload_btn\\\',	//自定义本地上传按钮\\r\\noFile:\\\'upload_file\\\',	//上传表单\\r\\noShowDiv:\\\'show_img\\\',	//上传前显示缩略图的容器\\r\\noPrevImg:\\\'prevImg\\\',	//为IE获取图片原始尺寸的预img\\r\\niMaxW:300,	//图片最大显示宽度	\\r\\niMaxH:300,	//图片最大显示高度\\r\\niLimitSize:5242880	//限制上传图片大小为5M=5242880 byte\\r\\n});\\r\\n};\\r\\n&lt;/script&gt;\\r\\n&lt;/head&gt;\\r\\n\\r\\n&lt;body&gt;\\r\\n&lt;input id=\\\"upload_file\\\" type=\\\"file\\\" /&gt;\\r\\n&lt;img id=\\\"upload_btn\\\" src=\\\"http://www.w3cfuns.com/data/attachment/album/201304/08/210035jmyx0e69l9bpxomh.jpg.thumb.jpg\\\" /&gt;\\r\\n&lt;div id=\\\"show_img\\\"&gt;\\r\\n&lt;img id=\\\"prevImg\\\" /&gt;\\r\\n&lt;/body&gt;\\r\\n&lt;/html&gt;\\r\\n\\r\\n</pre>\\r\\n<h4>\\r\\n	<span style=\\\"color:#0000f0;\\\"><span><span style=\\\"color:#000000;\\\">原文地址：</span><a href=\\\"http://www.w3cfuns.com/blog-5400368-5396043.html\\\" target=\\\"_blank\\\"><span style=\\\"color:#000000;\\\">http://www.w3cfuns.com/blog-5400368-5396043.html</span></a></span></span> \\r\\n</h4><div class=\\\"useWarning\\\" style=\\\"background-color: #f0f0f0\\\">\\n本文链接：https://www.92ez.com/?action=show&amp;id=140 <br>\\n                        <b>!!! 转载请先联系non3gov@gmail.com授权并在显著位置注明作者和原文链接 !!! <a href=\\\"https://www.92ez.com/black.html\\\" target=\\\"_blank\\\" style=\\\"color: #000;text-decoration: underline;background: none\\\">小黑屋</a> </b><br>\\n提示：技术文章有一定的时效性，请先确认是否适用你当前的系统环境。\\n</div>\\n\\n<style type=\\\"text/css\\\">\\n.useWarning a{color:#fff;background-color:#ea6f5a;border-radius:3px;padding:5px 10px;text-decoration: none;}\\n.tuijian{margin-top:10px;}\\n.tuijian a{display:block;}\\n.tuijian img{padding:0;margin:0;}\\n.tuijian a img{display:block;width:100%;}\\n.donateme{font-size: 14px;display:none;}\\n.donateme a{color: green;font-weight: bold;}\\n.useWarning{padding: 5px 10px;border: #c00 1px dashed;margin: 10px 0 0;color: #c00;}\\n</style>\\n</div>\')');
INSERT INTO `scrapy_yizhiyuan` VALUES (202, 'JS关闭子页面刷新父页面的方法', '(\'KBdancer\',\' 发表于 2013-05-04, 12:23 AM. \\n发表在：\\n\')', '(\'<div class=\\\"post-body\\\">\\n<p>\\r\\n	<br>\\r\\n</p>\\r\\n<p>\\r\\n	<span style=\\\"font-size:14px;\\\">js在父页中弹出页修改提交后，父页更新: <br>\\r\\n</span> \\r\\n</p>\\r\\n<pre class=\\\"brush:js;\\\">&lt;scriptlanguage=javascript&gt; functionwinclose(){ window.opener.location.reload(); window.cl...</pre>\\r\\n<p>\\r\\n	<span style=\\\"font-size:14px;\\\">在父页中弹出页修改提交后，父页更新:</span> \\r\\n</p>\\r\\n<pre class=\\\"brush:js;\\\">&lt;script language=javascript&gt;   \\r\\n function winclose() {   \\r\\n window.opener.location.reload();   \\r\\n window.close();   \\r\\n }   \\r\\n&lt;/script&gt;    \\r\\n&lt;input type=button name=close value=\\\"关闭\\\" onclick=winclose()&gt;   </pre>\\r\\n<p>\\r\\n	<span style=\\\"font-size:14px;\\\">方法二:</span><span style=\\\"font-size:14px;\\\"><span style=\\\"font-size:14px;\\\"></span></span> \\r\\n</p>\\r\\n<pre class=\\\"brush:js;\\\">window.opener.location.href=window.opener.location.href;window.close();  </pre>\\r\\n<p>\\r\\n	<span style=\\\"font-size:x-small;\\\"><span style=\\\"font-size:14px;\\\">2个方法都是刷新父窗口，但是其中还是有奥妙的哦。</span><br>\\r\\n<span style=\\\"font-size:14px;\\\">window.opener.location.reload();\\r\\n这个方法在强迫父窗口的时候，在有些IE浏览器（比如安全设置高）的情况下，会弹出一个确认对话框，提示是不是要重新再刷新一次页面，这可是比较郁闷的事\\r\\n情哦，我后来把这个方法替换成了 </span></span> \\r\\n</p>\\r\\n<pre class=\\\"brush:js;\\\">window.opener.location.href=window.opener.location.href;</pre>\\r\\n<span style=\\\"font-size:14px;\\\">就不会出现那样的问题了。</span> \\r\\n<p>\\r\\n	<br>\\r\\n</p>\\r\\n<p>\\r\\n	<span style=\\\"font-size:14px;\\\">window.opener\\r\\n其实是指本窗口的父窗口，比如，one.jsp \\r\\n通过popupwindow打开了two.jsp,哪么在two.jsp里面的window.opener就是指one.jsp，所以在two.jsp里\\r\\n面完全可以用window.opener调用任何一个one.jsp里面的方法，实现one.jsp和two.jsp的交互。</span> \\r\\n</p>\\r\\n<p>\\r\\n	<span style=\\\"font-size:x-small;\\\"><span><span style=\\\"font-size:x-small;\\\"><span style=\\\"font-size:14px;\\\">注意：</span><span style=\\\"font-size:14px;\\\">window.opener.location.href\\r\\n只是一个链接，如果想实现父窗口的提交就要调用window.opener.action=\\\"\\\" \\r\\n和window.opener.submit();方法，但是不幸的是这段代码在firefox下不能运行，解决的办法为在父窗口中写一个提交的\\r\\nfunction在子窗口中通过window.opener.functionname()调用。</span></span></span></span> \\r\\n</p>\\r\\n<p>\\r\\n	<span style=\\\"font-size:14px;\\\">通常在使用window.opener的时候要去判断父窗口的状态，如果父窗口被关闭或者更新，就会出错，解决办法是加上如下的验证if(window.opener &amp;&amp; !window.opener.closed)</span> \\r\\n</p>\\r\\n<p>\\r\\n	<span style=\\\"font-size:14px;\\\"><a href=\\\"http://www.w3cfuns.com/blog-5401627-5396037.html\\\" target=\\\"_blank\\\"></a><br>\\r\\n</span> \\r\\n</p>\\r\\n<p>\\r\\n	</p><p>\\r\\n		<span style=\\\"font-size:14px;\\\"><span style=\\\"font-size:14px;\\\">原文地址：</span><a href=\\\"http://www.w3cfuns.com/blog-5401627-5396037.html\\\" target=\\\"_blank\\\"><span style=\\\"font-size:14px;\\\">http://www.w3cfuns.com/blog-5401627-5396037.html</span></a></span> \\r\\n	</p>\\r\\n\\r\\n<p>\\r\\n	<br>\\r\\n</p>\\r\\n<br><div class=\\\"useWarning\\\" style=\\\"background-color: #f0f0f0\\\">\\n本文链接：https://www.92ez.com/?action=show&amp;id=130 <br>\\n                        <b>!!! 转载请先联系non3gov@gmail.com授权并在显著位置注明作者和原文链接 !!! <a href=\\\"https://www.92ez.com/black.html\\\" target=\\\"_blank\\\" style=\\\"color: #000;text-decoration: underline;background: none\\\">小黑屋</a> </b><br>\\n提示：技术文章有一定的时效性，请先确认是否适用你当前的系统环境。\\n</div>\\n\\n<style type=\\\"text/css\\\">\\n.useWarning a{color:#fff;background-color:#ea6f5a;border-radius:3px;padding:5px 10px;text-decoration: none;}\\n.tuijian{margin-top:10px;}\\n.tuijian a{display:block;}\\n.tuijian img{padding:0;margin:0;}\\n.tuijian a img{display:block;width:100%;}\\n.donateme{font-size: 14px;display:none;}\\n.donateme a{color: green;font-weight: bold;}\\n.useWarning{padding: 5px 10px;border: #c00 1px dashed;margin: 10px 0 0;color: #c00;}\\n</style>\\n</div>\')');
INSERT INTO `scrapy_yizhiyuan` VALUES (203, '移动端webapp开发必备知识', '(\'KBdancer\',\' 发表于 2013-05-04, 12:08 AM. \\n发表在：\\n\')', '(\'<div class=\\\"post-body\\\">\\n原作者：<a href=\\\"http://isux.tencent.com/%E7%A7%BB%E5%8A%A8%E7%BB%88%E7%AB%AF%E5%BC%80%E5%8F%91%E5%BF%85%E5%A4%87%E7%9F%A5%E8%AF%86.html\\\" target=\\\"_blank\\\">移动终端开发必备知识</a><br>\\r\\n<p>\\r\\n	转载请注明来源\\r\\n</p>\\r\\n<strong>一、基本概念</strong><strong>（1） CSS pixels与device pixels</strong>CSS pixels： 浏览器使用的抽象单位， 主要用来在网页上绘制内容。<br>\\r\\ndevice pixels： 显示屏幕的的最小物理单位，每个dp包含自己的颜色、亮度。<br>\\r\\n等值的 CSS pixels在手机屏幕上占多大的位置，这不是固定的，这取决于很多属性。经过分析和总结，我们可以得出这么一条公式： 1 CSS \\r\\npixels = （devicePixelRatio）^2 device pixels （^2是平方的意思，至于 \\r\\ndevicePixelRatio是什么东西，后面会讲解） 。<br>\\r\\n<p>\\r\\n	<strong>（2） PPI/DPI</strong>PPI，有时也叫DPI，所表示的是每英寸所拥有的像素（pixel）数目，数值越高，即代表\\r\\n显示屏能够以越高的密度显示图像。（注：这里的像素，指的是device \\r\\npixels。）搞清楚了PPI是什么意思，我们就能很容易理解PPI的计算方式了，我们需要首先算出手机屏幕的对角线等效像素，然后处以对角线（我们平\\r\\n常所说的手机屏幕尺寸就是说的手机屏幕对角线的长度），就可以得到PPI了。准确的计算公示大家可以参照下图。比较有意思的是，根据公式计算出来的\\r\\niPhone 4的PPI为330，要比苹果官方公布的326要高一点点。\\r\\n</p>\\r\\n<p>\\r\\n	<br>\\r\\n<img _load=\\\"1\\\" src=\\\"http://www.qianduan.net/wp-content/uploads/2012/11/072131F2S.jpg\\\" id=\\\"aimg_n4Q1O\\\" class=\\\"zoom\\\" alt=\\\"\\\" border=\\\"0\\\" height=\\\"300\\\" width=\\\"485\\\"><br>\\r\\n同理，以HTC G7为例，480*800的分辨率，3.7英寸，算出来就是252的PPI。<br>\\r\\n<strong>（3） 密度决定比例</strong>我们计算PPI就是为了知道一部手机设备是属于哪个密度区间的，因为不同的密度区间，对应着不同的默认缩放比例，这是一个很重要的概念。<br>\\r\\n<img _load=\\\"1\\\" src=\\\"http://www.qianduan.net/wp-content/uploads/2012/11/072132uQR.jpg\\\" id=\\\"aimg_N66Nq\\\" class=\\\"zoom\\\" alt=\\\"\\\" border=\\\"0\\\" height=\\\"426\\\" width=\\\"640\\\"><br>\\r\\n由上图可知，PPI在120-160之间的手机被归为低密度手机，160-240被归为中密度，240-320被归为高密度，320以上被归为超高密度（Apple给了它一个上流的名字——retina）。<br>\\r\\n这些密度对应着一个特定的缩放比例值，拿我们最熟悉的iphone4或4s来说，它们的PPI是326，属于超高密度的手机。当我们书写一个宽度为\\r\\n320px的页面放到iphone中显示，你会发现，它竟然是满宽的。这是因为，页面被默认放大了两倍，也就是640px，而iphone4或4s的宽，\\r\\n正是640px。<br>\\r\\n图中把高密度的一类圈起来，是因为这是android手机的统计数据，在国内安卓手机市场中，高密度的设备占了绝大多数的市场份额，这是很重要的一点，也是我们做安卓端webapp要注意的关键点。<br>\\r\\n<strong>（4） viewport的使用</strong>viewport总共有5个属性，分别如下：<br>\\r\\n	</p><table class=\\\"t_table\\\" cellspacing=\\\"0\\\">\\r\\n		<tbody>\\r\\n			<tr>\\r\\n				<td>\\r\\n					1234567\\r\\n				</td>\\r\\n				<td>\\r\\n					<span style=\\\"color:#009900;\\\">&lt;meta name=\\\"viewport\\\"</span><span style=\\\"color:#009900;\\\">content=\\\"height\\r\\n = [ pixel_value |device-height] ,width = [ pixel_value |device-width ] \\r\\n,initial-scale = float_value , minimum-scale = float_value , \\r\\nmaximum-scale = float_value ,user-scalable =[yes | no] ,target- \\r\\ndensitydpi = [ dpi_value | device-dpi| high-dpi | medium-dpi | low-dpi] \\\"\\r\\n /&gt;</span> \\r\\n				</td>\\r\\n			</tr>\\r\\n		</tbody>\\r\\n	</table>\\r\\n<br>\\r\\n在这些属性里面，我们重点关注<strong>target-densitydpi</strong>，这个属性可以改变设备的默认缩放。medium-\\r\\ndpi是target-densitydpi的默认值，如果我们显式定义target-densitydpi=device-dpi，那么设备就会按照真\\r\\n实的dpi来渲染页面。打个比方说，一张320*480的图片，放在iphone4里面，默认是占满屏幕的，但如果定义了target-\\r\\ndensitydpi=device-dpi，那么图片只占屏幕的四分之一（二分之一的平方），因为iphone4的分辨率是640*960。<br>\\r\\n<strong>二、解决方案</strong><strong>（1） 简单粗暴</strong>如果我们按照320px宽的设计稿去制作页面，并且\\r\\n不做任何的设置，页面会默认自动缩放到跟手机屏幕相等的宽度（这是由于 \\r\\nmedium-dpi是target-densitydpi的默认值，和不同密度对应不同缩放比例所决定的，这一切都是移动设备自动完成的 \\r\\n）。所以这种解决方案，简单，粗暴，有效。但有一个致命的缺点，对于高密度和超高密度的手机设备，页面（特别是图片）会失真，而且密度越多，失真越厉害。<br>\\r\\n<strong>（2） 极致完美</strong>在这种方案中，我们采用 \\r\\ntarget-densitydpi=device-dpi，这样一来，手机设备就会按照真实的像素数目来渲染，用专业的话来说，就是1 CSS \\r\\npixels = 1 device pixels。比如对于 640*960的 iphone，我们就可以做出 \\r\\n640*960的页面，在iphone上显示也不会有滚动条。当然，对于其他设备，也需制作不同尺寸的页面，所以这种方案往往是使用媒体查询来做成响应式\\r\\n的页面。这种方案可以在特定的分辨率下完美呈现，但是随着要兼容的不同分辨率越多，成本就越高，因为需要为每一种分辨率书写单独的代码。下面举个简单的例\\r\\n子：<br>\\r\\n	<table class=\\\"t_table\\\" cellspacing=\\\"0\\\">\\r\\n		<tbody>\\r\\n			<tr>\\r\\n				<td>\\r\\n					1\\r\\n				</td>\\r\\n				<td>\\r\\n					<span style=\\\"color:#009900;\\\">&lt;meta name=\\\"viewport\\\"content=\\\"target-densitydpi=device-dpi, width=device-width \\\" /&gt;</span> \\r\\n				</td>\\r\\n			</tr>\\r\\n		</tbody>\\r\\n	</table>\\r\\n<br>\\r\\n	<table class=\\\"t_table\\\" cellspacing=\\\"0\\\">\\r\\n		<tbody>\\r\\n			<tr>\\r\\n				<td>\\r\\n					1234567891011\\r\\n				</td>\\r\\n				<td>\\r\\n					<span style=\\\"color:#cc00cc;\\\">#header</span> <span style=\\\"color:#00AA00;\\\">{</span><span style=\\\"color:#000000;\\\"><strong>background</strong></span><span style=\\\"color:#00AA00;\\\">:</span><span style=\\\"color:#993333;\\\">url</span> <span style=\\\"color:#00AA00;\\\">(</span><span style=\\\"color:#ff0000;\\\"><i>medium-density-image.png</i></span><span style=\\\"color:#00AA00;\\\">)</span><span style=\\\"color:#00AA00;\\\">;</span><span style=\\\"color:#00AA00;\\\">}</span><span style=\\\"color:#a1a100;\\\">@media screen and (- webkit -device-pixel-ratio:1.5) {</span><span style=\\\"color:#808080;\\\"><i>/* CSS for high-density screens */</i></span><span style=\\\"color:#cc00cc;\\\">#header</span> <span style=\\\"color:#00AA00;\\\">{</span> <span style=\\\"color:#000000;\\\"><strong>background</strong></span><span style=\\\"color:#00AA00;\\\">:</span><span style=\\\"color:#993333;\\\">url</span> <span style=\\\"color:#00AA00;\\\">(</span><span style=\\\"color:#ff0000;\\\"><i>high-density-image.png</i></span><span style=\\\"color:#00AA00;\\\">)</span><span style=\\\"color:#00AA00;\\\">;</span><span style=\\\"color:#00AA00;\\\">}</span><span style=\\\"color:#00AA00;\\\">}</span><span style=\\\"color:#a1a100;\\\">@media screen and (- webkit -device-pixel-ratio:0.75) {</span><span style=\\\"color:#808080;\\\"><i>/* CSS for low-density screens */</i></span><span style=\\\"color:#cc00cc;\\\">#header</span> <span style=\\\"color:#00AA00;\\\">{</span> <span style=\\\"color:#000000;\\\"><strong>background</strong></span><span style=\\\"color:#00AA00;\\\">:</span><span style=\\\"color:#993333;\\\">url</span> <span style=\\\"color:#00AA00;\\\">(</span><span style=\\\"color:#ff0000;\\\"><i>low-density-image.png</i></span><span style=\\\"color:#00AA00;\\\">)</span><span style=\\\"color:#00AA00;\\\">;</span><span style=\\\"color:#00AA00;\\\">}</span><span style=\\\"color:#00AA00;\\\">}</span> \\r\\n				</td>\\r\\n			</tr>\\r\\n		</tbody>\\r\\n	</table>\\r\\n<br>\\r\\n<img _load=\\\"1\\\" src=\\\"http://www.qianduan.net/wp-content/uploads/2012/11/0721321AI.jpg\\\" id=\\\"aimg_N88sY\\\" class=\\\"zoom\\\" alt=\\\"\\\" border=\\\"0\\\" height=\\\"519\\\" width=\\\"513\\\"><br>\\r\\n<strong>（3） 合理折中</strong>针对安卓设备绝大多数是高密度，部分是中密度的特点，我们可以采用一个折中的方案：我们对480px\\r\\n宽的设计稿进行还原，但是页面制却做成320px宽（使用background-size来对图片进行缩小），然后，让页面自动按照比例缩放。这样一来，\\r\\n低密度的手机有滚动条（这种手机基本上已经没有人在用了），中密度的手机会浪费一点点流量，高密度的手机完美呈现，超高密度的手机轻微失真（超高密度的安\\r\\n卓手机很少）。这种方案的优点非常明显：只需要一套设计稿，一套代码（这里只是讨论安卓手机的情况）。<br>\\r\\n<strong>三、开发调试</strong><strong>（1） weinre远程实时调试</strong>Web开发者经常使用Firefox的firebug或者Chrome的开发人员工具进行Web调试，包括针对JavaScript，DOM元素和CSS样式的调试。但是，当我们期望为移动Web站点或应用进行调试时，这些工具就很难派上用场。<br>\\r\\n<a href=\\\"http://people.apache.org/%7Epmuellr/weinre/docs/latest/Home.html\\\" target=\\\"_blank\\\">weinre</a>就是一个帮助我们在桌面来远程调试运行在移动设备浏览器内的Web页面或应用的调试工具。weinre是WEb INspector REmote的简写，现在是Apache的一个开源项目，托管在<a href=\\\"https://github.com/apache/incubator-cordova-weinre\\\" target=\\\"_blank\\\">github</a>。<br>\\r\\n下面将介绍如此在日常工作使用它。<br>\\r\\n首先，我们要下载weinre的jar包——项目官方已经找不到该jar文件，网上能够找到，这里建议搭建个独立的web服务器，jar运行后是一个本地的服务器，和web服务器差不多~~<br>\\r\\n然后通过运行dos命令来启动它（<strong>请注意在你的电脑上已经安装有JDK</strong>）。运行命令如下，需要把路径改成你的实际文件位置：<br>\\r\\n	<table class=\\\"t_table\\\" cellspacing=\\\"0\\\">\\r\\n		<tbody>\\r\\n			<tr>\\r\\n				<td>\\r\\n					1\\r\\n				</td>\\r\\n				<td>\\r\\n					java <span style=\\\"color:#339933;\\\">-</span>jar d<span style=\\\"color:#339933;\\\">:</span>toolsweinre<span style=\\\"color:#339933;\\\">-</span>jarweinre.<span style=\\\"color:#660066;\\\">jar</span> –httpPort <span style=\\\"color:#CC0000;\\\">8081</span> –boundHost <span style=\\\"color:#339933;\\\">-</span>all<span style=\\\"color:#339933;\\\">-</span> <span style=\\\"color:#006600;\\\"><i>//（httpPort是指定服务端口，boundHost参数说明可以使用IP访问，all参数代表支持所有的host）。</i></span> \\r\\n				</td>\\r\\n			</tr>\\r\\n		</tbody>\\r\\n	</table>\\r\\n<br>\\r\\n<img _load=\\\"1\\\" src=\\\"http://www.qianduan.net/wp-content/uploads/2012/11/072133nfl.jpg\\\" id=\\\"aimg_uC2xm\\\" class=\\\"zoom\\\" alt=\\\"\\\" border=\\\"0\\\" height=\\\"248\\\" width=\\\"640\\\"><br>\\r\\n访问localhost:8081，如果看到如下的页面，说明weinre已经启动成功：<br>\\r\\n<img _load=\\\"1\\\" src=\\\"http://www.qianduan.net/wp-content/uploads/2012/11/072134TUa.jpg\\\" id=\\\"aimg_NCYyy\\\" class=\\\"zoom\\\" alt=\\\"\\\" border=\\\"0\\\" height=\\\"515\\\" width=\\\"640\\\"><br>\\r\\n输入debug client user \\r\\ninterface地址（调试客户端UI地址）。本例中即：http://localhost:8081/client/#anonymous，其\\r\\n中#anonymous是默认的调试id（debug id）。如果这个weinre调试服务器只是由你一个人使用，那么你可以使用默认的debug \\r\\nid：anonymous。 启动的weinre调试客户端ui如下图：<br>\\r\\n<img _load=\\\"1\\\" src=\\\"http://www.qianduan.net/wp-content/uploads/2012/11/0721352iK.jpg\\\" id=\\\"aimg_JgHC2\\\" class=\\\"zoom\\\" alt=\\\"\\\" border=\\\"0\\\" height=\\\"674\\\" width=\\\"640\\\"><br>\\r\\n在需要调试的页面加入中以下脚本：&lt;script type=”text/javascript” \\r\\nsrc=”http://localhost:8081/target/target-script-min.js#anonymous”&gt;&amp;\\r\\namp;lt;/script&gt;，注意把localhost换成手机能够访问的真实IP地址。当手机访问这个页面时，weinre客户端就会检测到\\r\\n目标设备，然后就可以对它进行调试了。<br>\\r\\n<img _load=\\\"1\\\" src=\\\"http://www.qianduan.net/wp-content/uploads/2012/11/072136xIK.jpg\\\" id=\\\"aimg_cX623\\\" class=\\\"zoom\\\" alt=\\\"\\\" border=\\\"0\\\" height=\\\"369\\\" width=\\\"640\\\"><br>\\r\\n因为手机上不方便截图，我这里就用两个浏览器窗口来展示效果，其实手机上的效果跟右边是一样的。<br>\\r\\n<strong>（2） AVD模拟器调试</strong>静态页面并不能满足我们的需求，很多实际效果比如touch事件，滚动事件，键盘输入事件\\r\\n等，都需要在真实的环境下测试，这时就需要用到模拟器。就像我们测试ie6一样，AVD模拟器可以类比于PC上的虚拟机，当我们需要测试某一特定的机型\\r\\n时，我们可以新建一个AVD，进行一系列的测试。不过使用AVD的前提是已经部署好android的开发环境，这个需要JDK + android \\r\\nSDK + Eclipse + ADT，还是稍微有点繁琐。<br>\\r\\n<strong>（3）手机抓包与配host</strong>在PC上，我们可以很方便地配host，但是手机上如何配host，这是一个问题。<br>\\r\\n这里主要使用fiddler和远程代理，实现手机配host的操作，具体操作如下：\\r\\n\\r\\n<ul class=\\\"litype_1\\\" type=\\\"1\\\">\\r\\n	<li>\\r\\n		首先，保证PC和移动设备在同一个局域网下；\\r\\n	</li>\\r\\n	<li>\\r\\n		PC上开启fiddler，并在设置中勾选“allow remote computers to connect”<img _load=\\\"1\\\" src=\\\"http://www.qianduan.net/wp-content/uploads/2012/11/072137w49.png\\\" id=\\\"aimg_UQoQ8\\\" class=\\\"zoom\\\" alt=\\\"\\\" border=\\\"0\\\" height=\\\"371\\\" width=\\\"562\\\"> \\r\\n	</li>\\r\\n	<li>\\r\\n		手机上设置代理，代理IP为PC的IP地址，端口为8888（这是fiddler的默认端口）。通常手机上可以直接设置代理，如果没有，可以去下载一个叫ProxyDroid的APP来实现代理的设置。\\r\\n	</li>\\r\\n	<li>\\r\\n		此时你会发现，用手机上网，走的其实是PC上的fiddler，所有的请求包都会在fiddler中列出来，配合willow使用，即可实现配host，甚至是反向代理的操作。<br>\\r\\n	</li>\\r\\n</ul>\\r\\n<strong> 总结：</strong>以上就是我们在实际开发中积累的一些经验和技巧，希望能够给大家一些帮助，如果你有好的方法或者工具，也请在留言中分享~~\\r\\n<p>\\r\\n	<br>\\r\\n</p><div class=\\\"useWarning\\\" style=\\\"background-color: #f0f0f0\\\">\\n本文链接：https://www.92ez.com/?action=show&amp;id=120 <br>\\n                        <b>!!! 转载请先联系non3gov@gmail.com授权并在显著位置注明作者和原文链接 !!! <a href=\\\"https://www.92ez.com/black.html\\\" target=\\\"_blank\\\" style=\\\"color: #000;text-decoration: underline;background: none\\\">小黑屋</a> </b><br>\\n提示：技术文章有一定的时效性，请先确认是否适用你当前的系统环境。\\n</div>\\n\\n<style type=\\\"text/css\\\">\\n.useWarning a{color:#fff;background-color:#ea6f5a;border-radius:3px;padding:5px 10px;text-decoration: none;}\\n.tuijian{margin-top:10px;}\\n.tuijian a{display:block;}\\n.tuijian img{padding:0;margin:0;}\\n.tuijian a img{display:block;width:100%;}\\n.donateme{font-size: 14px;display:none;}\\n.donateme a{color: green;font-weight: bold;}\\n.useWarning{padding: 5px 10px;border: #c00 1px dashed;margin: 10px 0 0;color: #c00;}\\n</style>\\n</div>\')');
INSERT INTO `scrapy_yizhiyuan` VALUES (204, 'jquery图片剪裁预览插件imgAreaSelect使用方法（第1讲）', '(\'KBdancer\',\' 发表于 2013-05-02, 10:33 PM. \\n发表在：\\n\')', '(\'<div class=\\\"post-body\\\">\\n<p>\\r\\n	在正式写代码之前，我想大家比较希望看到的是一个具备图片上传，然后出现预览图片这个效果，本文中暂时没有介绍图片上传这块，后面的文章会有上传+剪裁+预览合为一体的例子讲解，请耐心等待。我们先看基本用法。\\r\\n</p>\\r\\n<p>\\r\\n	首先，解压下载过来的插件包，保留css文件夹和scripts文件夹，其他的可以删掉，当然也可以保留，个人喜好而定。新建一个html文档，我这是第1讲，就叫做demo1.html吧，目录结构如下图所示：\\r\\n</p>\\r\\n<p>\\r\\n	<img src=\\\"data:image/png;base64,\\\" alt=\\\"\\\"> \\r\\n</p>\\r\\n<p>\\r\\n	我们打开这两个文件夹看看里面到底有啥宝贝。首先打开css文件夹，如图：\\r\\n</p>\\r\\n<p>\\r\\n	<img src=\\\"data:image/png;base64,\\\" alt=\\\"\\\"> \\r\\n</p>\\r\\n<p>\\r\\n	图中我们看到的有4个gif图片，这个是我们在选择区域时候看到的那个选取的边框的图片。两外3个css文件是操作选区时候一些样式的定义，一般我们使用default这个就行，当然，有兴趣的朋友也可以尝试使用另外两个css文件。接下来打开scripts文件夹，如图：\\r\\n</p>\\r\\n<p>\\r\\n	<img src=\\\"data:image/png;base64,\\\" alt=\\\"\\\"> \\r\\n</p>\\r\\n<p>\\r\\n	jquery.imgareaselect.js这个文件是一个开发用的文件，保留了基本的格式，开发者可以方便的对此文件进行修改或者扩展。\\r\\n</p>\\r\\n<p>\\r\\n	jquery.imgareaselect.min.js是对上面的开发包进行压缩过后的，也可以说是发行包。\\r\\n</p>\\r\\n<p>\\r\\n	jquery.imgareaselect.pack.js是对第一个文件压缩并混淆过的，压缩体积更小，方便网页的调用，但不适合开发。\\r\\n</p>\\r\\n<p>\\r\\n	jquery.min.js这个就不用说了吧，jquery的基础包。jquery的插件都依赖于这个基础包。\\r\\n</p>\\r\\n<p>\\r\\n	下面我们开始代码的编写。\\r\\n</p>\\r\\n<p>\\r\\n	使用文本编辑器打开demo1.html，写入基本的代码，引入需要的文件。\\r\\n</p>\\r\\n<p>\\r\\n	html代码：\\r\\n</p>\\r\\n<pre class=\\\"brush:html;\\\">&lt;!doctype html&gt;\\r\\n&lt;html&gt;\\r\\n&lt;head&gt;\\r\\n	&lt;title&gt;imgAreaSelect demo1&lt;/title&gt;\\r\\n	&lt;link rel=\\\"stylesheet\\\" type=\\\"text/css\\\" href=\\\"css/imgareaselect-default.css\\\" /&gt;\\r\\n  	&lt;script type=\\\"text/javascript\\\" src=\\\"scripts/jquery.min.js\\\"&gt;&lt;/script&gt;\\r\\n  	&lt;script type=\\\"text/javascript\\\" src=\\\"scripts/jquery.imgareaselect.pack.js\\\"&gt;&lt;/script&gt;\\r\\n  	&lt;script type=\\\"text/javascript\\\"&gt;\\r\\n  		//我的代码....\\r\\n  	&lt;/script&gt;\\r\\n&lt;/head&gt;\\r\\n&lt;body&gt;\\r\\n	\\r\\n&lt;/body&gt;\\r\\n&lt;/html&gt;</pre>\\r\\n<p>\\r\\n	我要实现的是现在剪裁区域的最大宽度和最大高度的效果，所以，要有图片才行。好的，引入一张图片。\\r\\n</p>\\r\\n<pre class=\\\"brush:html;\\\">&lt;img src=\\\"images/i.jpg\\\" id=\\\"bigPicture\\\" /&gt;</pre>\\r\\n<p>\\r\\n	在js里面我们需要初始化插件，调用插件的imgAreaSelect方法（注意大小写），代码这样写：\\r\\n</p>\\r\\n<p>\\r\\n	<br>\\r\\n</p>\\r\\n<pre class=\\\"brush:js;\\\">$(document).ready(function(){\\r\\n  	$(\\\"#bigPicture\\\").imgAreaSelect({\\r\\n\\r\\n  	});\\r\\n});</pre>\\r\\n最基础的代码就写好了，为了效果更加明显，我给图片加个边框\\r\\n<p>\\r\\n	<br>\\r\\n</p>\\r\\n<pre class=\\\"brush:html;\\\">&lt;img src=\\\"images/i.jpg\\\" id=\\\"bigPicture\\\" style=\\\"border:#000 1px solid;\\\" /&gt;</pre>\\r\\n来，看下效果吧：\\r\\n<p>\\r\\n	未选择区域的时候\\r\\n</p>\\r\\n<p>\\r\\n	<img src=\\\"data:image/png;base64,\\\" alt=\\\"\\\"> \\r\\n</p>\\r\\n<p>\\r\\n	使用鼠标拖拽出一个矩形区域后的效果。\\r\\n</p>\\r\\n<p>\\r\\n	<img src=\\\"data:image/png;base64,\\\" alt=\\\"\\\"> \\r\\n</p>\\r\\n<p>\\r\\n	是不是很酷，但是！！这能帮我们做什么呢？对，这对于我们来说毫无用处。所以我们必须想办法让它变得对我们来说有点用。怎么办呢，很简单，API上有一些属性和方法，我们不妨试着调用一下看看效果如何。\\r\\n</p>\\r\\n<p>\\r\\n	aspectRatio，我们来设置这个属性看看。这个属性是用来设定选取的长宽比，比如说16:9的黄金比例，试试看：\\r\\n</p>\\r\\n<pre class=\\\"brush:js;\\\">$(\\\"#bigPicture\\\").imgAreaSelect({\\r\\n	aspectRatio:\\\'16:9\\\'\\r\\n});</pre>\\r\\n效果图：\\r\\n<p>\\r\\n	<br>\\r\\n</p>\\r\\n<p>\\r\\n	<img src=\\\"data:image/png;base64,\\\" alt=\\\"\\\"> \\r\\n</p>\\r\\n<p>\\r\\n	所以，无论怎么拖拽选区的长宽比都是16：9的比例，下面我们来看handles这个属性，这个属性的值是true，false或者corners，当设置为true的时候，区域大小的调整手柄会出现在上下左右和四个角，设置为corners的时候手柄只会出现在角落，当设置为false的时候，不现实矩形状手柄，效果对比：\\r\\n</p>\\r\\n<p>\\r\\n	设置为true时候\\r\\n</p>\\r\\n<p>\\r\\n	<img src=\\\"data:image/png;base64,\\\" alt=\\\"\\\"> \\r\\n</p>\\r\\n<p>\\r\\n	设置为corners时候\\r\\n</p>\\r\\n<p>\\r\\n	<br>\\r\\n<img src=\\\"data:image/png;base64,\\\" alt=\\\"\\\"> \\r\\n</p>\\r\\n<p>\\r\\n	当设置为false的时候\\r\\n</p>\\r\\n<p>\\r\\n	<img src=\\\"data:image/png;base64,\\\" alt=\\\"\\\"> \\r\\n</p>\\r\\n<p>\\r\\n	下面我们来看x1,y1,x2,y2这个属性，这个属性是用来设置选区的初始位置，四个值分别代表着两个个坐标。我们来试一下:\\r\\n</p>\\r\\n<pre class=\\\"brush:js;\\\">$(\\\"#bigPicture\\\").imgAreaSelect({\\r\\n		x1: 120,y1: 90,x2: 280,y2: 210\\r\\n	});</pre>\\r\\n效果图：\\r\\n<p>\\r\\n	<br>\\r\\n</p>\\r\\n<p>\\r\\n	<img src=\\\"data:image/png;base64,\\\" alt=\\\"\\\"> \\r\\n</p>\\r\\n<p>\\r\\n	那接下来我们要做第三个效果，那就是限制最大的长和最大的宽\\r\\n</p>\\r\\n<p>\\r\\n	使用maxWidth和maxHeight来设定，当然也有最小宽度和最小高度，分别是minWidth和minHeight，这里只讲一个。\\r\\n</p>\\r\\n<p>\\r\\n	代码：\\r\\n</p>\\r\\n<p>\\r\\n	<br>\\r\\n</p>\\r\\n<pre class=\\\"brush:js;\\\">$(\\\"#bigPicture\\\").imgAreaSelect({  \\r\\n		maxWidth:100,maxHeight:200\\r\\n	});\\r\\n 效果图：\\r\\n</pre>\\r\\n<p>\\r\\n	<br>\\r\\n</p>\\r\\n<p>\\r\\n	<img src=\\\"data:image/png;base64,\\\" alt=\\\"\\\"> \\r\\n</p>\\r\\n<p>\\r\\n	好的，今天就先讲这三个效果，后面再继续深入研究其他效果。\\r\\n</p>\\r\\n<pre class=\\\"brush:js;\\\"></pre><div class=\\\"useWarning\\\" style=\\\"background-color: #f0f0f0\\\">\\n本文链接：https://www.92ez.com/?action=show&amp;id=110 <br>\\n                        <b>!!! 转载请先联系non3gov@gmail.com授权并在显著位置注明作者和原文链接 !!! <a href=\\\"https://www.92ez.com/black.html\\\" target=\\\"_blank\\\" style=\\\"color: #000;text-decoration: underline;background: none\\\">小黑屋</a> </b><br>\\n提示：技术文章有一定的时效性，请先确认是否适用你当前的系统环境。\\n</div>\\n\\n<style type=\\\"text/css\\\">\\n.useWarning a{color:#fff;background-color:#ea6f5a;border-radius:3px;padding:5px 10px;text-decoration: none;}\\n.tuijian{margin-top:10px;}\\n.tuijian a{display:block;}\\n.tuijian img{padding:0;margin:0;}\\n.tuijian a img{display:block;width:100%;}\\n.donateme{font-size: 14px;display:none;}\\n.donateme a{color: green;font-weight: bold;}\\n.useWarning{padding: 5px 10px;border: #c00 1px dashed;margin: 10px 0 0;color: #c00;}\\n</style>\\n</div>\')');
